mapper.select:
select

from problem
where p_delete_status = 1

    and p_no = #{pNo,jdbcType=VARCHAR}


    and p_title like concat('%',#{pTitle},'%')


    and p_type = #{pType,jdbcType=VARCHAR}


    and p_find_time = #{pFindTime,jdbcType=DATE}


    and p_person = #{pPerson,jdbcType=VARCHAR}


    and p_responsible = #{pResponsible,jdbcType=VARCHAR}


    and p_plan_time = #{pPlanTime,jdbcType=DATE}


    and p_status = #{pStatus,jdbcType=VARCHAR}
